const form = document.getElementById("interactiveForm");
form.addEventListener("submit", (e) => {
  e.preventDefault();
  let t = document.getElementById("name").value.trim(),
    l = document.getElementById("email").value.trim(),
    m = document.getElementById("role").value,
    a = document.getElementById("nameError"),
    s = document.getElementById("emailError"),
    d = !0;
  t ? a.classList.add("hidden") : (a.classList.remove("hidden"), (d = !1));
  let n = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  l && n.test(l)
    ? s.classList.add("hidden")
    : (s.classList.remove("hidden"), (d = !1)),
    m || (alert("Please select a role!"), (d = !1)),
    d &&
      (alert(`Form submitted successfully!
Name: ${t}
Email: ${l}
Role: ${m}`),
      (document.getElementById("name").value = ""),
      (document.getElementById("email").value = ""),
      (document.getElementById("role").value = ""));
});
