let minutes = 25,
  seconds = 0,
  run;
const timer = document.getElementById("timer"),
  startTimer = document.getElementById("start-btn");
startTimer.addEventListener("click", () => {
  run ||
    (run = setInterval(() => {
      if (0 === seconds) {
        if (((seconds = 59), 0 == (minutes -= 1))) {
          clearInterval(run), alert("It is time to take a break!");
          return;
        }
      } else seconds -= 1;
      (document.getElementById("seconds").textContent =
        seconds < 10 ? "0" + seconds : seconds),
        (document.getElementById("minutes").textContent =
          minutes < 10 ? "0" + minutes : minutes);
    }, 1e3));
});
const resetTimer = document.getElementById("reset-btn");
resetTimer.addEventListener("click", () => {
  clearInterval(run),
    (run = null),
    (minutes = 25),
    (seconds = 0),
    (document.getElementById("minutes").textContent = "25"),
    (document.getElementById("seconds").textContent = "00");
});
const goalInput = document.getElementById("goal-input");
goalInput.addEventListener("keydown", (e) => {
  "Enter" === e.key && settingGoals();
});
const addGoalBtn = document.getElementById("add-goal-btn");
addGoalBtn.addEventListener("click", () => settingGoals());
const goalList = document.getElementById("goal-list"),
  settingGoals = () => {
    let e = goalInput.value.trim(),
      t = document.createElement("li");
    (t.textContent = e),
      t.classList.add(
        "bg-green-100",
        "text-green-800",
        "px-4",
        "py-2",
        "rounded-lg",
        "shadow-md"
      ),
      goalList.appendChild(t),
      (goalInput.value = "");
  },
  motivationalQuotes = [
    "The best time to plant a tree was 20 years ago. The second-best time is now.",
    "Success is not final, failure is not fatal: It is the courage to continue that counts.",
    "Your time is limited, so don’t waste it living someone else’s life.",
    "Don’t watch the clock; do what it does. Keep going.",
    "Opportunities don't happen. You create them.",
  ],
  quote = document.getElementById("quote"),
  generateQuote = document.getElementById("new-quote-btn");
generateQuote.addEventListener("click", () => {
  quote.textContent = "";
  let e =
    motivationalQuotes[Math.floor(Math.random() * motivationalQuotes.length)];
  quote.textContent = e;
});
