const folderStructure = [
    {
      name: "Documents",
      subfolders: [
        { name: "Work" },
        { name: "Personal", subfolders: [{ name: "Photos" }] },
      ],
    },
    { name: "Downloads", subfolders: [{ name: "Projects" }] },
    {
      name: "Music",
      subfolders: [
        { name: "Dream Pop" },
        { name: "Jazz" },
        { name: "Hip Hop" },
      ],
    },
  ],
  createFolderList = (e) => {
    let l = document.createElement("ul");
    return (
      e.forEach((e) => {
        let r = document.createElement("li");
        if (
          ((r.innerHTML = e.name),
          r.classList.add("folder"),
          l.appendChild(r),
          e.subfolders)
        ) {
          let t = createFolderList(e.subfolders);
          (t.style.display = "none"),
            r.appendChild(t),
            r.addEventListener("click", (e) => {
              e.stopPropagation(),
                (t.style.display =
                  "none" === t.style.display ? "block" : "none");
            });
        }
      }),
      l
    );
  },
  folderList = createFolderList(folderStructure);
document.getElementById("folderStructure").appendChild(folderList);
