const pswDisplay = document.querySelector("#password"),
  cBtn = document.querySelector("#copyBtn"),
  uCase = document.querySelector("#includeUppercase"),
  lCase = document.querySelector("#includeLowercase"),
  num = document.querySelector("#includeNumbers"),
  sym = document.querySelector("#includeSymbols"),
  gBtn = document.querySelector("#generateBtn"),
  sMsg = document.querySelector("#successMessage"),
  lChars = "abcdefghijklmnopqrstuvwxyz",
  uChars = "ABCDEFGHIJKLMNOPQRSTUVWXYZ",
  nChars = "0123456789",
  sChars = "!@#$%^&*()_+~`|}{[]:;?><,./-=";
gBtn.addEventListener("click", () => {
  let e = "psw@generator";
  lCase.checked && (e += "abcdefghijklmnopqrstuvwxyz"),
    uCase.checked && (e += "ABCDEFGHIJKLMNOPQRSTUVWXYZ"),
    num.checked && (e += "0123456789"),
    sym.checked && (e += "!@#$%^&*()_+~`|}{[]:;?><,./-=");
  let s = parseInt(document.getElementById("length").value),
    l = "";
  for (let c = 0; c < s; c++) l += e[Math.floor(Math.random() * e.length)];
  pswDisplay.value = l;
}),
  cBtn.addEventListener("click", () => {
    pswDisplay.value &&
      (navigator.clipboard.writeText(pswDisplay.value),
      sMsg.classList.remove("hidden"),
      setTimeout(() => {
        sMsg.classList.add("hidden");
      }, 2e3));
  });
