// instruction.js ကို ပြန်လည် minimize ပြုလုပ်ထားပါသည်။

const noteInput = document.getElementById("noteInput"),
  addNoteBtn = document.getElementById("addNoteBtn"),
  notesContainer = document.getElementById("notesContainer");
let notes = [];
const renderingNotes = () => {
  (notesContainer.innerHTML = ""),
    notes.forEach((e, t) => {
      const n = document.createElement("div");
      n.classList =
        "p-4 bg-gray-200 rounded-md flex justify-between items-start";
      const o = document.createElement("p");
      (o.classList = "text-gray-800"), (o.textContent = e);
      const d = document.createElement("button");
      (d.classList =
        "bg-red-500 hover:bg-red-600 text-white font-semibold py-1 px-2 rounded-md"),
        (d.textContent = "Delete"),
        d.addEventListener("click", () => deleteNote(t)),
        n.append(o),
        n.append(d),
        notesContainer.appendChild(n);
    });
};
addNoteBtn.addEventListener("click", () => {
  const e = noteInput.value.trim();
  e && (notes.push(e), (noteInput.value = ""), renderingNotes());
});
const deleteNote = (e) => {
  notes.splice(e, 1), renderingNotes();
};
