const cityInput = document.getElementById("city-input"),
  searchBtn = document.getElementById("search-btn"),
  cityName = document.getElementById("city-name"),
  temperature = document.getElementById("temperature"),
  condition = document.getElementById("condition");
cityInput.addEventListener("keydown", (t) => {
  "Enter" === t.key && get_Weather_Info();
}),
  searchBtn.addEventListener("click", () => {
    get_Weather_Info();
  });
const get_Weather_Info = async () => {
  let t = cityInput.value.trim(),
    e = `https://api.openweathermap.org/data/2.5/weather?q=${t}&appid=6594ce1a6044c12069ca4cb67ee9cbc3&units=metric`;
  try {
    let n = await fetch(e);
    if (!n.ok) throw Error("City not found!");
    let a = await n.json();
    (cityName.textContent = `${a.name}`),
      (temperature.textContent = `Temperature: ${a.main.temp} \xb0C`),
      (condition.textContent = `Condition: ${a.weather[0].description}`);
  } catch (i) {
    console.error(i.message), alert(i.message);
  }
};
