window.onload = () => {
  displayEachProverb();
};
const proverbContainer = document.getElementById("proverb-container"),
  displayEachProverb = async () => {
    try {
      let e = await fetch("hundred-burmese-proverbs.json"),
        r = await e.json();
      r.forEach((e) => {
        let r = document.createElement("div");
        r.classList.add("proverb");
        let n = document.createElement("p");
        (n.innerHTML = `<strong>Proverb:</strong> ${e.proverb}`),
          r.appendChild(n);
        let a = document.createElement("p");
        (a.innerHTML = `<strong>Translation:</strong> ${e.translation}`),
          r.appendChild(a),
          proverbContainer.appendChild(r);
      });
    } catch (n) {
      console.error(n.message), alert(n.message);
    }
  };
