const first_operation = (e, t, n) => t ** 2 - 4 * e * n,
  second_operation = (e, t, n) => {
    let o = first_operation(e, t, n);
    if (o < 0) return null;
    {
      let l = (-t + Math.sqrt(o)) / (2 * e),
        a = (-t - Math.sqrt(o)) / (2 * e);
      return [l, a];
    }
  };
function quadratic_equation() {
  let e = parseFloat(document.getElementById("a").value),
    t = parseFloat(document.getElementById("b").value),
    n = parseFloat(document.getElementById("c").value),
    o = second_operation(e, t, n),
    l = document.getElementById("result");
  if (o) {
    let [a, r] = o;
    (l.textContent = `The solutions of x are ${a.toFixed(2)} or ${r.toFixed(
      2
    )}.`),
      (l.className = "mt-6 text-green-600 font-semibold text-center");
  } else
    (l.textContent = "The solutions of x are undefined."),
      (l.className = "mt-6 text-red-600 font-semibold text-center");
  (document.getElementById("a").value = ""),
    (document.getElementById("b").value = ""),
    (document.getElementById("c").value = "");
}
