let open = !1;
document.getElementById("hamburger").addEventListener("click", () => {
  let t = document.getElementById("mobile-menu");
  t.classList.toggle("hidden"),
    t.classList.add("transition", "ease-in-out", "duration-300"),
    (open = !t.classList.contains("hidden"))
      ? (document
          .querySelector(".top")
          .classList.add(
            "rotate-45",
            "translate-y-1",
            "transition",
            "duration-300"
          ),
        document.querySelector(".middle").classList.remove("block"),
        document
          .querySelector(".bottom")
          .classList.add(
            "-rotate-45",
            "-translate-y-1",
            "transition",
            "duration-300"
          ))
      : (document
          .querySelector(".top")
          .classList.remove(
            "rotate-45",
            "translate-y-1",
            "transition",
            "duration-500"
          ),
        document.querySelector(".middle").classList.add("block"),
        document
          .querySelector(".bottom")
          .classList.remove(
            "-rotate-45",
            "-translate-y-1",
            "transition",
            "duration-500"
          ));
});
