const imageFile = document.getElementById("imageInput"),
  previewImage = document.getElementById("previewImage"),
  grayscaleAdjust = document.getElementById("grayscale"),
  blurAdjust = document.getElementById("blur"),
  brightnessAdjust = document.getElementById("brightness"),
  contrastAdjust = document.getElementById("contrast"),
  resetButton = document.getElementById("resetButton"),
  adjustImage = () => {
    let e = grayscaleAdjust.value,
      t = blurAdjust.value,
      s = brightnessAdjust.value,
      a = contrastAdjust.value;
    previewImage.style.filter = `grayscale(${e}%) blur(${t}px) brightness(${s}%) contrast(${a}%)`;
  };
imageFile.addEventListener("change", (e) => {
  let t = e.target.files[0];
  if (!t) return;
  let s = new FileReader();
  s.addEventListener("load", () => {
    (previewImage.src = s.result), previewImage.classList.remove("hidden");
  }),
    s.readAsDataURL(t);
}),
  grayscaleAdjust.addEventListener("input", adjustImage),
  blurAdjust.addEventListener("input", adjustImage),
  brightnessAdjust.addEventListener("input", adjustImage),
  contrastAdjust.addEventListener("input", adjustImage),
  resetButton.addEventListener("click", () => {
    (grayscaleAdjust.value = 0),
      (blurAdjust.value = 0),
      (brightnessAdjust.value = 100),
      (contrastAdjust.value = 100),
      (previewImage.style.filter = "none");
  });
